const INITAL_STATE ={
  currentUser: null
}

const userReducer = (state = INITAL_STATE, action) =>{
  switch(action.type){
    //name of state var
    case 'SET_CURRENT_USER':
      return {
        ...state,
        //state var = payload
        currentUser: action.payload
      }

    default:
      return state;
  }
}
export default userReducer;
