export const addItemToCart = (cartItems, newCartItem) => {

  const existingCart = cartItems.find(
    cartItem => cartItem.id === newCartItem.id);

    //if true then update quantity
    if(existingCart){
      return cartItems.map(cartItem => 
        cartItem.id === newCartItem.id 
        ? {...cartItem, quantity: cartItem.quantity + 1}
        : cartItem
        )
    }

  return [...cartItems, {...newCartItem, quantity: 1}]

  };

  export const removeItem =(cartItems, rmItem) =>{

    const existingCart = cartItems.find(
      cartItem => cartItem.id === rmItem.id);

      if(existingCart && rmItem.quantity > 1){
        return cartItems.map(cartItem => 
          cartItem.id === rmItem.id 
          ? {...cartItem, quantity: cartItem.quantity -1}
          : cartItem
          )
      }
      if(existingCart && rmItem.quantity === 1){
        return cartItems.filter(
          cartItem => cartItem.id !== rmItem.id
        )
      }

      return [...cartItems]

  };
